import React from 'react';
import './food.css';

const foodDescription = {
  imageSrc: '',
  flavour: 'Mozzarella',
  description: 'Mozzarella and Basil.',
  price: 18,
}

let quantity = 0;

function incrementValue() {
  quantity++;
};

function decrementValue() {
  if (quantity < 0) {
    quantity = 0;
  } else {
    quantity--;
  }
};

export default function Food(props) {
  return (
    <div className="Food">
      <div className="image-container">
        <img src={foodDescription.imageSrc} alt='pizza' />
      </div>
      <div className="Food-information">
        <div className="Food-description">
          <h2>{foodDescription.flavour}</h2>
          <p>{foodDescription.description}</p>
          <p>{foodDescription.price}</p>

          <div className="Food-quantity">
            <input type="button" onclick={decrementValue()} className="Food-button1" variant="contained" color="primary">
              -
            </input>
            <input type="text" id="number" value="0">{quantity}</input>
            <input type="button" onclick={"incrementValue()"} className="Food-button2" variant="contained" color="primary">
              +
            </input>
          </div>

        </div>
      </div>
    </div>
  )
};
