import React from 'react';
import './counter.css'

class Counter extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      count: 0, 
      type: 'number' 
    }

  };

  increment() {
    this.setState({
      count: this.state.count + 1
    });
  };

  decrement() {
    if (this.state.count > 0) {
      this.setState(prevState => ({ count: prevState.counter - 1 }))
    };
  };

  render() {
    return (
      <div>
        <div />
        <input type='button' className="sub" onClick={this.decrement.bind(this)} value="-" />
        <label for="counter" className="counter">{this.state.count}</label>
        <input type='button' className="add" onClick={this.increment.bind(this)} value="+" />
      </div>
    )
  }
};

export default Counter;
